import React, {useState} from "react";
import AvailableMeals from "./AvailableMeals";
import classes from "./MealHeadings.module.css";
import Card from "../UI/Card";

import Tabs from "@mui/material/Tabs";
import Tab from "@mui/material/Tab";

const mealHeadings = [
  {
    id: 1,
    name: "Burgers",
  },
  {
    id: 2,
    name: "Sides",
  },
  {
    id: 3,
    name: "Drinks",
  },
];

function tabChangeHandler(event) {
  setSelectedTab(event.currentTarget.textContent);
}

function MealHeadings() {

  const [selectedTab, setSelectedTab] = useState("Burgers");

  const mealsList = mealHeadings.map((meal) => <Tab label={meal.name} />);

  return (
    <section className={classes.mealHeadings}>
      <Card>
        <Tabs
          // value={value}
          onChange={tabChangeHandler}
          aria-label="basic tabs example"
        >
          {mealsList}
        </Tabs>
        <AvailableMeals />
      </Card>
    </section>

    // <section className={classes.meals}>
    //   <Card>
    //     <ul>{mealsList}</ul>
    //   </Card>
    // </section>
  );
}

export default MealHeadings;
