function Meals() {
  return (
    <React.Fragment>
      <MealsSummary />
      <Tabs
        value={value}
        onChange={handleChange}
        aria-label="basic tabs example"
      >
        <Tab label="Item One" {...a11yProps(0)} />
        <Tab label="Item Two" {...a11yProps(1)} />
        <Tab label="Item Three" {...a11yProps(2)} />
      </Tabs>
      <Mealheadings />
      {/* <AvailableMeals /> */}
    </React.Fragment>
  );
}

export default Meals;
